$('.list-of-five').slick({
  speed: 300,
  infinite: false,
  slidesToShow: 5,
  slidesToScroll: 5,
  autoplay:true,
  arrows: true,
  prevArrow:"<button type='button' class='slick-prev'><i class='fas fa-angle-left' aria-hidden='true'></i></button>",
  nextArrow:"<button type='button' class='slick-next'><i class='fas fa-angle-right' aria-hidden='true'></i></button>",
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 3
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2
      }
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1
      }
    }
  ]
});

